import axios from "axios";
import React, { useEffect,useState } from "react";
import { Link, useParams } from "react-router-dom";
import "../ViewPatient.css";

export default function ViewUser() {
  const [patient, setPatient] = useState({
    name: "",
    username: "",
    email: "",
    postcode:"",
    symptoms:""
  });

  const { id } = useParams();

  useEffect(() => {
    loadPatient();
  }, []);

  const loadPatient = async () => {
    const result = await axios.get(`http://localhost:8080/patient/${id}`);
    setPatient(result.data);
  };

  const deletePatient = async (id) => {

  };

  const onClick = async (e) => {
    e.preventDefault();
    await axios.delete(`http://localhost:8080/patient/${id}`);
    loadPatient();
    navigate("/");

  };

  return (

    <div className="container">
    <h2 className="text-center mb-4">Patient Details</h2>

    <div className="row justify-content-center">
      <div className="col-md-6">
        <div className="card">
          <div className="card-header">
            Details of patient: {patient.id}
          </div>
          <ul className="list-group list-group-flush">
            <li className="list-group-item">
              <b>Name:</b> {patient.name}
            </li>
            <li className="list-group-item">
              <b>UserName:</b> {patient.username}
            </li>
            <li className="list-group-item">
              <b>Email:</b> {patient.email}
            </li>
            <li className="list-group-item">
              <b>Postcode:</b> {patient.postcode}
            </li>
            <li className="list-group-item">
              <b>Symptoms:</b> {patient.symptoms}
            </li>
          </ul>
        </div>
      </div>
    </div>

    <div className="row justify-content-center mt-4">
      <div className="col-md-6">
      <button className="Delete" onClick={() => deletePatient(patient.id)}>
          Delete
        </button>
        <Link className="btn btn-primary" to={"/"}>
          Back to Home
        </Link>
      </div>
    </div>
  </div>

  );
}
